// Skills.jsx
import React from 'react';

function Skills() {
  const skillCategories = [
    {
      title: "Flutter & Dart",
      skills: [
        { name: "Flutter Framework", level: 95 },
        { name: "Dart Language", level: 90 },
        { name: "State Management", level: 85 },
        { name: "Animations", level: 80 },
        { name: "Custom Widgets", level: 90 }
      ]
    },
    {
      title: "Backend & APIs",
      skills: [
        { name: "Firebase", level: 85 },
        { name: "RESTful APIs", level: 90 },
        { name: "GraphQL", level: 75 },
        { name: "Node.js", level: 70 },
        { name: "MongoDB", level: 65 }
      ]
    },
    {
      title: "Development Tools",
      skills: [
        { name: "Git & GitHub", level: 85 },
        { name: "CI/CD", level: 75 },
        { name: "Testing", level: 80 },
        { name: "VS Code/Android Studio", level: 90 },
        { name: "Agile/Scrum", level: 85 }
      ]
    }
  ];

  return (
    <div className="container mx-auto px-6">
      <div className="text-center mb-16">
        <h2 className="text-3xl font-bold mb-4">My Skills</h2>
        <div className="w-20 h-1 bg-green-400 mx-auto mb-6"></div>
        <p className="text-gray-300 max-w-2xl mx-auto">
          I've honed my skills in Flutter development and related technologies to deliver exceptional mobile applications.
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
        {skillCategories.map((category, index) => (
          <div key={index} className="bg-gray-800 rounded-lg p-6 shadow-lg hover:shadow-green-900/20 transition-50">
            <h3 className="text-xl font-bold mb-4 text-green-400">{category.title}</h3>
            <div className="space-y-4">
              {category.skills.map((skill, skillIndex) => (
                <div key={skillIndex}>
                  <div className="flex justify-between mb-1">
                    <span className="text-gray-300">{skill.name}</span>
                    {/* <span className="text-green-400">{skill.level}%</span> */}
                  </div>
                  {/* <div className="w-full bg-gray-700 rounded-full h-2">
                    <div
                      className="bg-green-400 h-2 rounded-full"
                      style={{ width: `${skill.level}%` }}
                    />
                  </div> */}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Skills;
